';
	$context['subaccount_dropdown'] = '';
	if (!empty($user_info['subaccounts']))
	{
		$context['subaccount_dropdown'] = '
			<form  action="' . $scripturl . '?action=switchsubaccount" method="post" name="subaccount_drop" id="subaccount_drop" enctype="multipart/form-data">
